let parsedResult;

try {
  // Paso 1: Obtener el texto crudo generado por Gemini
  const rawText = $json.candidates[0].content.parts[0].text;

  // Paso 2: Limpiar texto (quitar comillas, etiquetas "json", espacios, etc.)
  const cleanedText = rawText
    .replace(/^"json/, '') // elimina encabezado "json
    .replace(/^json/, '')
    .replace(/^```json/, '')
    .replace(/^```/, '')
    .replace(/```$/, '')
    .trim();

  // Paso 3: Mostrar en consola
  console.log("‚úÖ Texto limpio:", cleanedText);

  // Paso 4: Intentar parsear el JSON limpio
  parsedResult = JSON.parse(cleanedText);
  console.log("‚úÖ JSON parseado correctamente:", parsedResult);

} catch (error) {
  console.error("‚ùå Error al parsear JSON:", error.message);
  parsedResult = {
    intent: "listar_todo",
    filters: [],
    message: "No se pudo interpretar el mensaje del usuario."
  };
}

// Paso 5: Retornar salida estandarizada
return [
  {
    json: {
      intent: parsedResult.intent || "listar_todo",
      filters: parsedResult.filters || [],
      message: parsedResult.message || ""
    }
  }
];

// Obtener la consulta del usuario
const query = (items[0].json.queryMaterial || "").toLowerCase().trim();

// Separar todas las palabras clave
const keywords = query.split(/\s+/).filter(k => k);

// Funci√≥n que valida si un campo contiene todas las palabras clave
function fieldContainsAll(fields, keywords) {
  const combined = fields.join(" ").toLowerCase();
  return keywords.every(kw => combined.includes(kw));
}

// Filtrar resultados
const results = items.filter(item => {
  const desc = item.json.Descripci√≥n || "";
  const name = item.json.Nombre || "";
  const color = item.json.Color || "";

  // Si no hay palabras clave, mostrar todo
  if (keywords.length === 0) return true;

  // Validar si todas las keywords est√°n en alg√∫n campo combinado
  return fieldContainsAll([desc, name, color], keywords);
});

// Armar respuesta
let responseMessage = "";

if (results.length > 0) {
  responseMessage = `‚úÖ Aqu√≠ tienes los productos que coinciden con tu b√∫squeda "${query}":\n\n`;
  results.forEach(row => {
    responseMessage += `‚≠ê *${row.json.Nombre || 'Sin nombre'}*\n`;
    responseMessage += `üîπ SKU: ${row.json.SKU || 'N/A'}\n`;
    responseMessage += `üîπ Color: ${row.json.Color || 'N/A'}\n`;
    responseMessage += `üîπ Tama√±o/Formato: ${row.json.Nombre?.match(/\d+\*\d+/)?.[0] || 'N/A'}\n`;
    responseMessage += `üîπ Uso sugerido: ${row.json.Descripci√≥n?.match(/(interior|exterior|fachadas|ba√±os|cocinas|rec√°maras|muros|pisos)/gi)?.join(", ") || 'No especificado'}\n`;
    responseMessage += `üîπ Descripci√≥n: ${row.json.Descripci√≥n || 'N/A'}\n`;
    responseMessage += `-----------------------------------\n`;
  });
} else {
  responseMessage = `‚ö†Ô∏è No encontr√© productos que coincidan con tu b√∫squeda "${query}". Prueba con otras palabras clave como material, color o tama√±o.`;
}

return [{ json: { finalResponse: responseMessage } }];

const productos = $input.all();
const resultados = [];

function normalizarTexto(texto) {
  return texto?.toString()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/[\n\r\t\u2028\u2029]/g, ' ')
    .trim()
    .toLowerCase();
}

const queryField = "descripcion";
const queryValue = normalizarTexto(items[0].json.queryValue);

for (const producto of productos) {
  const campoProducto = Object.keys(producto.json).find(k =>
    k.toLowerCase().includes(queryField)
  );

  if (!campoProducto) continue;

  const descripcionActual = normalizarTexto(producto.json[campoProducto]);

  console.log(`üîç Comparando descripci√≥n: esperado='${queryValue}' vs actual='${descripcionActual}'`);

  if (descripcionActual.includes(queryValue)) {
    console.log("‚úÖ Producto v√°lido. Agregado.");
    resultados.push(producto);
  } else {
    console.log("‚ùå No coincide. Producto descartado.");
  }
}

return resultados;

const productos = $input.all();
const filtros = [];
const resultados = [];

// Obtener filtros desde el input
for (const entrada of items) {
  if (entrada.json.queryField && entrada.json.queryValue) {
    filtros.push({
      campo: entrada.json.queryField,
      valor: entrada.json.queryValue,
    });
  }
}

console.log(`üì¶ Productos cargados: ${productos.length}`);
console.log(`üß™ Filtros recibidos:`, filtros);

if (filtros.length === 0) {
  console.log("‚ö†Ô∏è No se encontraron filtros. Retornando todos los productos.");
  return productos;
}

function normalizarTexto(texto) {
  return texto?.toString()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/[\n\r\t\u2028\u2029]/g, ' ')
    .trim()
    .toLowerCase();
}

for (const producto of productos) {
  let cumpleTodos = true;

  for (const filtro of filtros) {
    const campoReal = filtro.campo;
    const valorEsperado = normalizarTexto(filtro.valor);

    // Buscar clave del producto que coincida sin importar may√∫sculas
    const campoKey = Object.keys(producto.json).find(k => k.toLowerCase() === campoReal.toLowerCase());
    const valorActual = normalizarTexto(producto.json[campoKey]);

    console.log(`üîç Comparando campo '${campoReal}': esperado='${valorEsperado}' vs actual='${valorActual}'`);

    if (!valorActual || !valorActual.includes(valorEsperado)) {
      console.log("‚ùå No coincide. Producto descartado.");
      cumpleTodos = false;
      break;
    }
  }

  if (cumpleTodos) {
    console.log("‚úÖ Producto v√°lido. Agregado.");
    resultados.push(producto);
  }
}

return resultados;

const productos = $input.all();
const entrada = items[0].json;
const resultados = [];

const campo = (entrada.queryField || "").toLowerCase().trim();
const valor = (entrada.queryValue || "").toLowerCase().trim();

function normalizar(texto) {
  return texto?.toString()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/[\n\r\t]/g, ' ')
    .trim()
    .toLowerCase();
}

for (const producto of productos) {
  const p = producto.json;
  let coincide = false;

  if (campo === "color" && normalizar(p.Color).includes(valor)) {
    coincide = true;
  } else if (campo === "sku" && normalizar(p.SKU).includes(valor)) {
    coincide = true;
  } else if (campo === "nombre" && normalizar(p.Nombre).includes(valor)) {
    coincide = true;
  } else if (campo === "metros por caja" && normalizar(p["Metros por caja"]).includes(valor)) {
    coincide = true;
  } else {
    // Si no es campo directo, buscar en Descripci√≥n, Nombre y Vetas
    const combined = `${p.Descripci√≥n} ${p.Nombre} ${p.Vetas}`.toLowerCase();
    if (combined.includes(valor)) {
      coincide = true;
    }
  }

  if (coincide) {
    resultados.push(producto);
  }
}

return resultados;

